mapper.select:
SELECT 

FROM park_intf_log a

WHERE a.id = #{id}

SELECT 

FROM park_intf_log a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.intf_name = #{intfName}


		AND a.call_method = #{callMethod}


		AND a.order_id = #{orderId}


		AND a.caller = #{caller}


		AND a.callee = #{callee}


		AND a.req_time BETWEEN #{beginReqTime} AND #{endReqTime}


		AND a.rsp_time BETWEEN #{beginRspTime} AND #{endRspTime}


		AND a.call_status = #{callStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM park_intf_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE park_intf_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
