mapper.select:
SELECT 

FROM test_memo a

WHERE a.id = #{id}

SELECT 

FROM test_memo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.content = #{content}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM test_memo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE test_memo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
